$(function(){var e=$(".txp-search");e.find('[type="search"]').bind("focus click",function(){e.find('[type="search"]').addClass("active").andSelf().find(".txp-search-dropdown").show()});e.bind("mouseleave",function(){e.find('[type="search"]').removeClass("active").andSelf().find(".txp-search-dropdown").hide()});e.find(".txp-search-dropdown").find('label[for="search-all"]').prev().bind("click",function(){$(this).parent().siblings().find(":checkbox").attr("checked",this.checked).attr("disabled",this.checked)})});